function AccessibleButton({ onClick, children }) {
  return (
    <button
      type="button"
      onClick={onClick}
      aria-pressed="false"
      tabIndex={0}
      style={{ cursor: 'pointer' }}
    >
      {children}
    </button>
  );
}

import { ReactComponent as Logo } from './logo.svg';

function Header() {
  return (
    <header>
      <Logo width={48} height={48} />
      <h1>App Title</h1>
    </header>
  );
}
